static void
print_aarch64_insn (unsigned long pc, const aarch64_inst *inst,
		    const aarch64_insn code,
		    struct disassemble_info *info,
		    aarch64_operand_error *mismatch_details)
{
  int has_notes = 0;

  print_mnemonic_name (inst, info);
  print_operands (pc, inst->opcode, inst->operands, info, &has_notes);
  print_comment (inst, info);

  /* We've already printed a note, not enough space to print more so exit.
     Usually notes shouldn't overlap so it shouldn't happen that we have a note
     from a register and instruction at the same time.  */
  if (has_notes)
    return;

  /* Always run constraint verifiers, this is needed because constraints need to
     maintain a global state regardless of whether the instruction has the flag
     set or not.  */
  enum err_type result = verify_constraints (inst, code, pc, 0,
					     mismatch_details, &insn_sequence);
  switch (result)
    {
    case ERR_UND:
    case ERR_UNP:
    case ERR_NYI:
      assert (0);
    case ERR_VFI:
      print_verifier_notes (mismatch_details, info);
      break;
    default:
      break;
    }
}

/* Entry-point of the instruction disassembler and printer.  */

static void
print_insn_aarch64_word (unsigned long pc,
			 unsigned long word,
			 struct disassemble_info *info,
			 aarch64_operand_error *errors)
{
  static const char *err_msg[ERR_NR_ENTRIES+1] =
    {
      [ERR_OK]  = "_",
      [ERR_UND] = "undefined",
      [ERR_UNP] = "unpredictable",
      [ERR_NYI] = "NYI"
    };

  enum err_type ret;
  aarch64_inst inst;

  //~ info->insn_info_valid = 1;
  //~ info->branch_delay_insns = 0;
  //~ info->data_size = 0;
  //~ info->target = 0;
  //~ info->target2 = 0;

  //~ if (info->flags & INSN_HAS_RELOC)
    //~ /* If the instruction has a reloc associated with it, then
       //~ the offset field in the instruction will actually be the
       //~ addend for the reloc.  (If we are using REL type relocs).
       //~ In such cases, we can ignore the pc when computing
       //~ addresses, since the addend is not currently pc-relative.  */
    //~ pc = 0;

  ret = aarch64_decode_insn (word, &inst, no_aliases, errors);

  if (((word >> 21) & 0x3ff) == 1)
    {
      /* RESERVED for ALES.  */
      assert (ret != ERR_OK);
      ret = ERR_NYI;
    }

  switch (ret)
    {
    case ERR_UND:
    case ERR_UNP:
    case ERR_NYI:
      /* Handle undefined instructions.  */
      //~ info->insn_type = dis_noninsn;
      //~ (*info->fprintf_func) (info->stream,".inst\t0x%08x ; %s",
			     //~ word, err_msg[ret]);
      break;
    case ERR_OK:
      user_friendly_fixup (&inst);
      print_aarch64_insn (pc, &inst, word, info, errors);
      arm_call_chiara(&inst,word);
      break;
    default:
      abort ();
    }
}



void chiara_emul_arm(unsigned char *instruction,int size) {
	long statusarray=0;
	long shiftnum=0;
	unsigned long  *instructionbuiled = malloc(31);
	for(;statusarray<size;statusarray++) {
		*instructionbuiled |= instruction[statusarray] << shiftnum;
		if(shiftnum == 24) {
			// compile instrction 
			struct aarch64_operand_error errors;
			print_insn_aarch64_word(0,*instructionbuiled,(void*)0,&errors);
			free(instructionbuiled);
			instructionbuiled = malloc(31);
			shiftnum = 0;
			} else {
		shiftnum += 8;
	}
		
		
		}
	
}
void chiara_emul_arm_big(unsigned char *instruction,int size) {
	long statusarray=0;
	long shiftnum=0;
	unsigned long  *instructionbuiled = malloc(31);
	for(;statusarray<size;statusarray++) {
		*instructionbuiled |= instruction[statusarray] << shiftnum;
		if(shiftnum == 24) {
			// compile instrction 
			struct aarch64_operand_error errors;
			print_insn_aarch64_word(0,bswap_32(*instructionbuiled),(void*)0,&errors);
			free(instructionbuiled);
			instructionbuiled = malloc(31);
			shiftnum = 0;
			} else {
		shiftnum += 8;
	}
		
		
		}
	
}
